    //   <TweetList
    //     tweets={tweets}
    //     username={username}
    //     removeTweet={removeTweet}
    //   />
    // </div>

    <div>
      <div className="card text-center">
        <div className="card-header">
          <h1>{username}'s Twitter</h1>
        </div>
        <hr/>
        <div class="card-body">
          <Form handleSubmit={handleSubmit}/>
          <hr/>
        </div>
         <TweetList
          tweets={tweets}
          username={username}
          removeTweet={removeTweet}
        />          
      </div>
    </div>


  );
};

export default TwitterPage;
