import React, { useState } from "react";
import './css/Form.css'

const Form = (props) => {
  const [tweet, setTweet] = useState("");

  const handleChange = (e) => {
    setTweet(e.target.value);
  };

  const submitForm = () => {
    props.handleSubmit(tweet);
    setTweet("");
  };

  return (
    <form className="card tweet-box">
      <label className= "card-header" htmlFor="tweet">New Tweet: </label>
      <input
        className="tweet-box-input"
        placeholder="What's happening?"
        type="text"
        name="tweet"
        id="tweet"
        value={tweet}
        onChange={handleChange}
      />
      <input className= "card-header-pills tweet-box-button" type="button" value="Submit" onClick={submitForm} />
    </form>
  );
};

export default Form;
